import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import session from "express-session";
import passport from "passport";
import { Strategy as GoogleStrategy } from "passport-google-oauth20";
import { storage } from "./storage";
import { insertUserSchema, insertTeamSchema, insertTeamRequestSchema, insertMessageSchema } from "@shared/schema";
import { z } from "zod";

// Configure session
const sessionConfig = {
  secret: process.env.SESSION_SECRET || "your-secret-key",
  resave: false,
  saveUninitialized: false,
  cookie: {
    secure: process.env.NODE_ENV === "production",
    maxAge: 24 * 60 * 60 * 1000 // 24 hours
  }
};

// Configure Google OAuth
passport.use(new GoogleStrategy({
  clientID: process.env.GOOGLE_CLIENT_ID || "",
  clientSecret: process.env.GOOGLE_CLIENT_SECRET || "",
  callbackURL: "/api/auth/google/callback"
}, async (accessToken, refreshToken, profile, done) => {
  try {
    const email = profile.emails?.[0]?.value;
    if (!email) return done(new Error("No email found"), undefined);

    // Check if domain is allowed (not gmail.com)
    const domain = email.split('@')[1];
    if (domain === 'gmail.com') {
      return done(new Error("Gmail accounts are not allowed. Please use your institutional email."), undefined);
    }

    // Check if user exists
    let user = await storage.getUserByGoogleId(profile.id);

    if (!user) {
      // Create new user
      user = await storage.createUser({
        googleId: profile.id,
        email: email,
        name: profile.displayName || "",
        avatar: profile.photos?.[0]?.value,
        university: domain.includes('vit') ? 'VIT University' : domain.split('.')[0].toUpperCase(),
        isProfileComplete: false
      });
    }

    return done(null, user);
  } catch (error) {
    return done(error, undefined);
  }
}));

passport.serializeUser((user: any, done) => {
  done(null, user.id);
});

passport.deserializeUser(async (id: string, done) => {
  try {
    const user = await storage.getUser(id);
    done(null, user);
  } catch (error) {
    done(error, null);
  }
});

export async function registerRoutes(app: Express): Promise<Server> {
  app.use(session(sessionConfig));
  app.use(passport.initialize());
  app.use(passport.session());

  // Auth routes
  app.get("/api/auth/google", passport.authenticate("google", {
    scope: ["profile", "email"]
  }));

  app.get("/api/auth/google/callback", 
    passport.authenticate("google", { failureRedirect: "/?error=auth_failed" }),
    (req, res) => {
      const user = req.user as any;
      if (!user.isProfileComplete) {
        res.redirect("/profile");
      } else {
        res.redirect("/dashboard");
      }
    }
  );

  app.post("/api/auth/logout", (req, res) => {
    req.logout(() => {
      res.json({ success: true });
    });
  });

  app.get("/api/auth/me", (req, res) => {
    if (req.user) {
      res.json(req.user);
    } else {
      res.status(401).json({ error: "Not authenticated" });
    }
  });

  // User routes
  app.post("/api/users/profile", async (req, res) => {
    if (!req.user) return res.status(401).json({ error: "Not authenticated" });

    try {
      const userData = insertUserSchema.partial().parse(req.body);
      const updatedUser = await storage.updateUser((req.user as any).id, {
        ...userData,
        isProfileComplete: true
      });
      res.json(updatedUser);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : "Invalid data" });
    }
  });

  app.get("/api/users", async (req, res) => {
    if (!req.user) return res.status(401).json({ error: "Not authenticated" });

    try {
      const users = await storage.getAllUsers();
      res.json(users.map(user => ({
        id: user.id,
        name: user.name,
        username: user.username,
        avatar: user.avatar,
        university: user.university,
        department: user.department,
        skills: user.skills,
        bio: user.bio
      })));
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch users" });
    }
  });

  app.get("/api/users/username/:username", async (req, res) => {
    try {
      const user = await storage.getUserByUsername(req.params.username);
      res.json({ available: !user });
    } catch (error) {
      res.status(500).json({ error: "Failed to check username" });
    }
  });

  // Team routes
  app.get("/api/teams", async (req, res) => {
    if (!req.user) return res.status(401).json({ error: "Not authenticated" });

    try {
      const { category } = req.query;
      let teams;

      if (category && typeof category === 'string') {
        teams = await storage.getTeamsByCategory(category);
      } else {
        teams = await storage.getRecommendedTeams((req.user as any).id);
      }

      res.json(teams);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch teams" });
    }
  });

  app.get("/api/teams/my", async (req, res) => {
    if (!req.user) return res.status(401).json({ error: "Not authenticated" });

    try {
      const teams = await storage.getTeamsByUser((req.user as any).id);
      res.json(teams);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch user teams" });
    }
  });

  app.post("/api/teams", async (req, res) => {
    if (!req.user) return res.status(401).json({ error: "Not authenticated" });

    try {
      const teamData = insertTeamSchema.parse({
        ...req.body,
        leaderId: (req.user as any).id
      });
      const team = await storage.createTeam(teamData);
      res.json(team);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : "Invalid data" });
    }
  });

  app.get("/api/teams/:id", async (req, res) => {
    if (!req.user) return res.status(401).json({ error: "Not authenticated" });

    try {
      const team = await storage.getTeam(req.params.id);
      if (!team) return res.status(404).json({ error: "Team not found" });

      const members = await storage.getTeamMembers(team.id);
      res.json({ ...team, members });
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch team" });
    }
  });

  // Team request routes
  app.post("/api/teams/:id/join", async (req, res) => {
    if (!req.user) return res.status(401).json({ error: "Not authenticated" });

    try {
      const requestData = insertTeamRequestSchema.parse({
        teamId: req.params.id,
        userId: (req.user as any).id,
        message: req.body.message
      });
      const request = await storage.createTeamRequest(requestData);
      res.json(request);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : "Invalid data" });
    }
  });

  app.get("/api/teams/:id/requests", async (req, res) => {
    if (!req.user) return res.status(401).json({ error: "Not authenticated" });

    try {
      const team = await storage.getTeam(req.params.id);
      if (!team) return res.status(404).json({ error: "Team not found" });
      if (team.leaderId !== (req.user as any).id) {
        return res.status(403).json({ error: "Not authorized" });
      }

      const requests = await storage.getTeamRequests(req.params.id);
      res.json(requests);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch requests" });
    }
  });

  app.put("/api/team-requests/:id", async (req, res) => {
    if (!req.user) return res.status(401).json({ error: "Not authenticated" });

    try {
      const { status } = z.object({ status: z.enum(["approved", "rejected"]) }).parse(req.body);
      const request = await storage.updateTeamRequest(req.params.id, status);

      // If approved, add user to team
      if (status === "approved") {
        await storage.addTeamMember({
          teamId: request.teamId,
          userId: request.userId,
          status: "approved",
          role: "member"
        });
      }

      res.json(request);
    } catch (error) {
      res.status(400).json({ error: error instanceof Error ? error.message : "Invalid data" });
    }
  });

  // Message routes
  app.get("/api/teams/:id/messages", async (req, res) => {
    if (!req.user) return res.status(401).json({ error: "Not authenticated" });

    try {
      // Check if user is a member of the team
      const members = await storage.getTeamMembers(req.params.id);
      const isMember = members.some(member => member.userId === (req.user as any).id && member.status === "approved");

      if (!isMember) {
        return res.status(403).json({ error: "Not a team member" });
      }

      const messages = await storage.getMessages(req.params.id);
      res.json(messages);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch messages" });
    }
  });

  // Events routes
  app.get("/api/events", async (req, res) => {
    try {
      const events = await storage.getActiveEvents();
      res.json(events);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch events" });
    }
  });

  const httpServer = createServer(app);

  // WebSocket setup for real-time chat
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  wss.on('connection', (ws: WebSocket, req) => {
    console.log('New WebSocket connection');

    ws.on('message', async (data) => {
      try {
        const message = JSON.parse(data.toString());

        if (message.type === 'chat_message') {
          // Save message to database
          const savedMessage = await storage.createMessage(insertMessageSchema.parse({
            teamId: message.teamId,
            userId: message.userId,
            content: message.content
          }));

          // Broadcast to all connected clients
          wss.clients.forEach((client) => {
            if (client.readyState === WebSocket.OPEN) {
              client.send(JSON.stringify({
                type: 'chat_message',
                message: savedMessage
              }));
            }
          });
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
      }
    });

    ws.on('close', () => {
      console.log('WebSocket connection closed');
    });
  });

  return httpServer;
}

server/storage.ts.content:
import { 
  users, teams, teamMembers, teamRequests, messages, events,
  type User, type InsertUser, type Team, type InsertTeam, 
  type TeamMember, type InsertTeamMember, type TeamRequest, type InsertTeamRequest,
  type Message, type InsertMessage, type Event, type InsertEvent
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, or, ilike, not, inArray } from "drizzle-orm";

export interface IStorage {
  // User methods
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  getUserByGoogleId(googleId: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, user: Partial<InsertUser>): Promise<User>;
  getAllUsers(): Promise<User[]>;

  // Team methods
  getTeam(id: string): Promise<Team | undefined>;
  getTeamsByUser(userId: string): Promise<Team[]>;
  getTeamsByCategory(category: string): Promise<Team[]>;
  getRecommendedTeams(userId: string): Promise<Team[]>;
  createTeam(team: InsertTeam): Promise<Team>;
  updateTeam(id: string, team: Partial<InsertTeam>): Promise<Team>;

  // Team member methods
  getTeamMembers(teamId: string): Promise<TeamMember[]>;
  getTeamMembersByUser(userId: string): Promise<TeamMember[]>;
  addTeamMember(member: InsertTeamMember): Promise<TeamMember>;
  updateTeamMember(teamId: string, userId: string, status: string): Promise<TeamMember>;
  removeTeamMember(teamId: string, userId: string): Promise<void>;

  // Team request methods
  getTeamRequests(teamId: string): Promise<TeamRequest[]>;
  getTeamRequestsByUser(userId: string): Promise<TeamRequest[]>;
  createTeamRequest(request: InsertTeamRequest): Promise<TeamRequest>;
  updateTeamRequest(id: string, status: string): Promise<TeamRequest>;

  // Message methods
  getMessages(teamId: string): Promise<Message[]>;
  createMessage(message: InsertMessage): Promise<Message>;

  // Event methods
  getAllEvents(): Promise<Event[]>;
  getActiveEvents(): Promise<Event[]>;
  createEvent(event: InsertEvent): Promise<Event>;
}

export class DatabaseStorage implements IStorage {
  // User methods
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user || undefined;
  }

  async getUserByGoogleId(googleId: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.googleId, googleId));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(insertUser).returning();
    return user;
  }

  async updateUser(id: string, insertUser: Partial<InsertUser>): Promise<User> {
    const [user] = await db.update(users).set({
      ...insertUser,
      updatedAt: new Date()
    }).where(eq(users.id, id)).returning();
    return user;
  }

  async getAllUsers(): Promise<User[]> {
    return await db.select().from(users);
  }

  // Team methods
  async getTeam(id: string): Promise<Team | undefined> {
    const [team] = await db.select().from(teams).where(eq(teams.id, id));
    return team || undefined;
  }

  async getTeamsByUser(userId: string): Promise<Team[]> {
    return await db.select({
      id: teams.id,
      name: teams.name,
      description: teams.description,
      leaderId: teams.leaderId,
      maxMembers: teams.maxMembers,
      requiredSkills: teams.requiredSkills,
      category: teams.category,
      eventName: teams.eventName,
      deadline: teams.deadline,
      isActive: teams.isActive,
      createdAt: teams.createdAt,
      updatedAt: teams.updatedAt,
    })
    .from(teams)
    .innerJoin(teamMembers, eq(teams.id, teamMembers.teamId))
    .where(and(eq(teamMembers.userId, userId), eq(teamMembers.status, "approved")));
  }

  async getTeamsByCategory(category: string): Promise<Team[]> {
    return await db.select().from(teams).where(and(eq(teams.category, category), eq(teams.isActive, true)));
  }

  async getRecommendedTeams(userId: string): Promise<Team[]> {
    // For now, return all active teams that the user is not already a member of
    const userTeams = await this.getTeamsByUser(userId);
    const userTeamIds = userTeams.map(team => team.id);

    if (userTeamIds.length > 0) {
      return await db.select().from(teams).where(
        and(
          eq(teams.isActive, true),
          not(inArray(teams.id, userTeamIds))
        )
      );
    } else {
      return await db.select().from(teams).where(eq(teams.isActive, true));
    }
  }

  async createTeam(insertTeam: InsertTeam): Promise<Team> {
    const [team] = await db.insert(teams).values(insertTeam).returning();

    // Add the creator as the team leader
    await this.addTeamMember({
      teamId: team.id,
      userId: team.leaderId,
      status: "approved",
      role: "leader"
    });

    return team;
  }

  async updateTeam(id: string, insertTeam: Partial<InsertTeam>): Promise<Team> {
    const [team] = await db.update(teams).set({
      ...insertTeam,
      updatedAt: new Date()
    }).where(eq(teams.id, id)).returning();
    return team;
  }

  // Team member methods
  async getTeamMembers(teamId: string): Promise<TeamMember[]> {
    return await db.select().from(teamMembers).where(eq(teamMembers.teamId, teamId));
  }

  async getTeamMembersByUser(userId: string): Promise<TeamMember[]> {
    return await db.select().from(teamMembers).where(eq(teamMembers.userId, userId));
  }

  async addTeamMember(member: InsertTeamMember): Promise<TeamMember> {
    const [teamMember] = await db.insert(teamMembers).values(member).returning();
    return teamMember;
  }

  async updateTeamMember(teamId: string, userId: string, status: string): Promise<TeamMember> {
    const [teamMember] = await db.update(teamMembers).set({ status })
      .where(and(eq(teamMembers.teamId, teamId), eq(teamMembers.userId, userId)))
      .returning();
    return teamMember;
  }

  async removeTeamMember(teamId: string, userId: string): Promise<void> {
    await db.delete(teamMembers)
      .where(and(eq(teamMembers.teamId, teamId), eq(teamMembers.userId, userId)));
  }

  // Team request methods
  async getTeamRequests(teamId: string): Promise<TeamRequest[]> {
    return await db.select().from(teamRequests).where(eq(teamRequests.teamId, teamId));
  }

  async getTeamRequestsByUser(userId: string): Promise<TeamRequest[]> {
    return await db.select().from(teamRequests).where(eq(teamRequests.userId, userId));
  }

  async createTeamRequest(request: InsertTeamRequest): Promise<TeamRequest> {
    const [teamRequest] = await db.insert(teamRequests).values(request).returning();
    return teamRequest;
  }

  async updateTeamRequest(id: string, status: string): Promise<TeamRequest> {
    const [teamRequest] = await db.update(teamRequests).set({ status })
      .where(eq(teamRequests.id, id)).returning();
    return teamRequest;
  }

  // Message methods
  async getMessages(teamId: string): Promise<Message[]> {
    return await db.select().from(messages)
      .where(eq(messages.teamId, teamId))
      .orderBy(desc(messages.createdAt));
  }

  async createMessage(message: InsertMessage): Promise<Message> {
    const [msg] = await db.insert(messages).values(message).returning();
    return msg;
  }

  // Event methods
  async getAllEvents(): Promise<Event[]> {
    return await db.select().from(events).orderBy(desc(events.startDate));
  }

  async getActiveEvents(): Promise<Event[]> {
    return await db.select().from(events)
      .where(eq(events.isActive, true))
      .orderBy(desc(events.startDate));
  }

  async createEvent(event: InsertEvent): Promise<Event> {
    const [evt] = await db.insert(events).values(event).returning();
    return evt;
  }
}

export const storage = new DatabaseStorage();

